INSERT INTO `t_sys_notic` VALUES ('gzzd5209818e-ef6d-11e3-a783-474ca86607ee', 'wsdx', 'gzzd', '0', '普通', '0', '', '党员思想政治工作制度', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; COLOR: black; mso-themecolor: text1\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>1</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; COLOR: black; mso-themecolor: text1\">、党支部在党委的指导下，积极主动地配合行政领导做好本单位党员、群众的政治思想工作，经常研究分析党员、群众的思想状况，教育引导他们在思想上、政治上与党中央保持一致，确保政令畅通。<SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN></SPAN></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; COLOR: black; mso-themecolor: text1\"><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN></SPAN></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; COLOR: black; mso-themecolor: text1\"><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN></SPAN></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; COLOR: black; mso-themecolor: text1\"><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>2</SPAN>、思想政治工作围绕党的中心工作和本单位的业务工作，<SPAN lang=EN-US><?xml:namespace prefix = \"o\" ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; COLOR: black; mso-themecolor: text1\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; COLOR: black; mso-themecolor: text1; mso-hansi-font-family: Calibri; mso-hansi-theme-font: minor-latin; mso-bidi-font-family: \'Times New Roman\'; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA\"><SPAN style=\"mso-spacerun: yes\"></SPAN><