INSERT INTO `t_sys_notic` VALUES ('ssywcdae054d3f784311', '', 'ssyw', '0', '普通', '0', ';wsdx;', '我校获国家级大学生创新创业训练计划实施工作先进单位', '<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;转重邮主页网讯（教务处供稿&nbsp; 编审&nbsp; 敖永春）</strong>10月18日至19日，由教育部、科技部主办的第七届全国大学生创新创业年会在西安交通大学举行。我校作为重庆市唯一一所高校，被重庆市教委选派参与工作交流和研讨，同时被会议推选为先进集体，获“2012—2014年度国家级大学生创新创业训练计划实施工作先进单位”称号。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 全国大学生创新创业年会是集中展示全国大学生创新创业训练计划成绩的一个窗口，本次年会吸引了来自全国近300所院校、700余名师生代表参与，240项国家级大学生创新创业训练计划项目参与大会交流，项目了覆盖理、工、农、医、文、法等12个学科门类。大学生创新创业训练计划内容包括创新训练项目、创业训练项目和创业实践项目三类，按层次分为校级、市级、国家级三级。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我校从2010年开始，通过设立校级大学生创新性实验训练计划，启动了大学生创新教育改革工作，截止2014年6月共有486项项目予以立项建设，其中国家级12项，市级72项，校级402项。在开展此项工作过程中，学校坚持将创新创业教育与学校教学改革实践结合，力争通过“创新创业训练计划”项目实施，努力推进学校在创新性人才培养新模式上的改革，实现一个转变、两个核心，即“促进以学生为主体的人才培养教育思想转变，开展探索性实验和研究性学习，建立以问题和课题为核心的教学模式”的教学改革目标。</p><p style=\"TEXT-ALIGN: center\"><a href=\"http://xwzx.cqupt.edu.cn/xwzx/upload/201410/20141024174144543.jpg\" target=\"_blank\"><img style=\"BORDER-TOP: #333333 0px double; BORDER-RIGHT: #333333 0px double; BACKGROUND: #f1f1f1; BORDER-BOTTOM: #333333 0px double; PADDING-BOTTOM: 4px; TEXT-ALIGN: center; PADDING-TOP: 4px; PADDING-LEFT: 4px; MARGIN: 10px auto; BORDER-LEFT: #333333 0px double; DISPLAY: block; PADDING-RIGHT: 4px\" border=\"0\" src=\"http://xwzx.cqupt.edu.cn/xwzx/upload/201410/20141024174144543_s.jpg\"/></a></p><p style=\"TEXT-ALIGN: center\">我校获国家级大学生创新创业训练计划实施工作先进单位<strong>（教务处 供图）</strong></p><p></p>', '0', '0', '', '已发布', '0', '', '113', '学生处', '0102277', '学生处', '2014-10-30 09:20:52', '0102277', '高延春', '2014-10-30 09:20:48');
INSERT INTO `t_sys_notic` VALUES ('ssywcfc5f99c1f1d4b33', 'wsdx', 'ssyw', '0', '普通', '0', '', '学校党校2014年秋季学期正式开班', '<p><strong>（组织部供稿&nbsp; 编审&nbsp; 敖永春）</strong>10月19日，学校党校2014年秋季学期正式开班，校党委书记方海洋、党委副书记游敏惠分别为培训学员作专题报告。</p><p>方海洋为培训学员作了《党的宗旨》的专题报告。报告解读了“党的宗旨”和“为人民服务”两个概念，从精神、物质和制度保障三个层面分析了“为人民服务”的内涵，并从政党与“为人民服务”的关系和政体与“为人民服务”的关系两个维度进行了对比分析，报告提纲挈领、逻辑严密、引经据典，学员深受启发。</p><p>开班典礼在第二国际会议厅举行，游敏惠出席典礼并作专题报告。开班典礼上，游敏惠向参加培训学习的学员表示祝贺，她指出，党校培训是学校英才培养计划的一部分，希望全体学员要珍惜学习机会，努力提高学习效果，在平时的学习生活中争做榜样，争取早日加入党组织。开班典礼结束后，游敏惠为全体学员作了《坚定理想信念、履行党员义务》的专题报告。报告分别从党校学习的意义、重要性、入党应注意的问题和入党程序四个方面做了详细的讲解，报告内容丰富、案例生动，学员深受鼓舞和教育。</p><p>党校秋季学期培训班包括入党积极分子培训班和预备党员培训班两个班次，将历时一个多月，通过专题讲座、影视教学、分组讨论、经验交流等形式，从党的基本知识、党的方针政策、党风党纪、加强党性修养等方面对入党积极分子和预备党员进行培训。学校各分党校也同时举行了开班典礼。</p><p style=\"TEXT-ALIGN: center\"><a href=\"http://xwzx.cqupt.edu.cn/xwzx/upload/201410/20141021181332596.jpg\" target=\"_blank\"><img style=\"BORDER-BOTTOM: #333333 0px double; TEXT-ALIGN: center; BORDER-LEFT: #333333 0px double; PADDING-BOTTOM: 4px; MARGIN: 10px auto; PADDING-LEFT: 4px; PADDING-RIGHT: 4px; DISPLAY: block; BACKGROUND: #f1f1f1; BORDER-TOP: #333333 0px double; BORDER-RIGHT: #333333 0px double; PADDING-TOP: 4px\" border=\"0\" src=\"http://xwzx.cqupt.edu.cn/xwzx/upload/201410/20141021181332596_s.jpg\"/></a></p><p style=\"TEXT-ALIGN: center\">校党委书记方海洋为培训学员作专题报告<strong>（记者&nbsp;任远敬 摄）</strong></p><p style=\"TEXT-ALIGN: center\"><a href=\"http://xwzx.cqupt.edu.cn/xwzx/upload/201410/20141021181415332.jpg\" target=\"_blank\"><img style=\"BORDER-BOTTOM: #333333 0px double; TEXT-ALIGN: center; BORDER-LEFT: #333333 0px double; PADDING-BOTTOM: 4px; MARGIN: 10px auto; PADDING-LEFT: 4px; PADDING-RIGHT: 4px; DISPLAY: block; BACKGROUND: #f1f1f1; BORDER-TOP: #333333 0px double; BORDER-RIGHT: #333333 0px double; PADDING-TOP: 4px\" border=\"0\" src=\"http://xwzx.cqupt.edu.cn/xwzx/upload/201410/20141021181415332_s.jpg\"/></a></p><p style=\"TEXT-ALIGN: center\">校党委副书记游敏惠出席开班典礼并为培训学员作专题报告<strong>（记者 袁如亮 摄）</strong></p><p></p>', '0', '0', '', '已发布', '0', '', 'cqupt', '重庆邮电大学', 'admin', '重庆邮电大学', '2014-10-23 09:33:11', 'admin', '管理员', '2014-10-23 09:33:06');
