INSERT INTO `t_sys_notic` VALUES ('swtzdb853d56e455435a', '', 'swtz', '0', '普通', '0', ';wsdx;', '关于认真组织学习贯彻“习近平总书记教师节重要讲话精神”的通知', '<p style=\"TEXT-ALIGN: left; MARGIN: 0px 7px; LINE-HEIGHT: 44px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">校属各单位：</span></p><p style=\"TEXT-ALIGN: left; MARGIN: 0px 7px; LINE-HEIGHT: 44px; TEXT-INDENT: 43px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">在第30个全国教师节之际，习近平总书记到北京师范大学看望教师学生，并发表重要讲话，号召全国广大教师要有理想信念、有道德情操、有扎实学识、有仁爱之心，做党和人民满意的好老师。习总书记的讲话在全社会引起强烈反响。为进一步贯彻落实习总书记重要讲话精神，现将中共重庆市委教育工委转发《中共教育部党组关于教育系统学习贯彻习近平总书记教师节重要讲话精神的通知》<span style=\"FONT-SIZE: 21px; COLOR: #444444\"><span style=\"FONT-FAMILY: Times New Roman\">(</span></span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">渝教工委〔</span><span style=\"FONT-SIZE: 21px; COLOR: #444444\"><span style=\"FONT-FAMILY: Times New Roman\">2014</span></span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">〕</span><span style=\"FONT-SIZE: 21px; COLOR: #444444\"><span style=\"FONT-FAMILY: Times New Roman\">80</span></span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">号</span><span style=\"FONT-SIZE: 21px; COLOR: #444444\"><span style=\"FONT-FAMILY: Times New Roman\">) </span></span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">转发给你们，请结合通知要求，按照《中共重庆邮电大学委员会2014-2015学年第一学期政治学习安排》，认真组织本单位教职员工学习习总书记讲话原文，积极开展讨论，引导全体教职员工<span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">当好中华民族“梦之队”的筑梦人，争做党和人民满意的好教师。</span></span></span></p><p style=\"TEXT-ALIGN: left; MARGIN: 0px 7px; LINE-HEIGHT: 44px; TEXT-INDENT: 43px\">&nbsp;</p><p style=\"TEXT-ALIGN: left; TEXT-INDENT: 43px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">请各单位做好学习讨论记录备查。</span></p><p style=\"LINE-HEIGHT: 16px\"><img style=\"VERTICAL-ALIGN: middle; MARGIN-RIGHT: 2px\" src=\"http://222.177.140.105/system/ueditor/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"(2014)A620+转发中共教育部党组关于教育系统学习贯彻习近平总书记教师节重要讲话精神的通知_98615357.doc\" style=\"FONT-SIZE: 12px; COLOR: #0066cc\" href=\"/AttFile/file/20141030/1414632858794012020.doc\">(2014)A620+转发中共教育部党组关于教育系统学习贯彻习近平总书记教师节重要讲话精神的通知_98615357.doc</a></p><p style=\"TEXT-ALIGN: left; TEXT-INDENT: 43px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">&nbsp;</span></p><p style=\"TEXT-ALIGN: left; TEXT-INDENT: 43px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">&nbsp;</span></p><p style=\"TEXT-ALIGN: left; TEXT-INDENT: 43px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">&nbsp;</span></p><p style=\"TEXT-ALIGN: left; TEXT-INDENT: 43px\"><span style=\"FONT-SIZE: 21px; COLOR: #444444\"><span style=\"FONT-FAMILY: Times New Roman\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">党委宣传部</span></p><p style=\"TEXT-ALIGN: left; TEXT-INDENT: 43px\"><span style=\"FONT-SIZE: 21px; COLOR: #444444\"><span style=\"FONT-FAMILY: Times New Roman\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 2014</span></span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">年</span><span style=\"FONT-SIZE: 21px; COLOR: #444444\"><span style=\"FONT-FAMILY: Times New Roman\">10</span></span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">月</span><span style=\"FONT-SIZE: 21px; COLOR: #444444\"><span style=\"FONT-FAMILY: Times New Roman\">10</span></span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: #444444\">日</span></p><p></p>', '0', '0', '', '已发布', '0', '', '113', '学生处', '0102277', '学生处', '2014-10-30 09:34:27', '0102277', '高延春', '2014-10-30 09:34:25');
INSERT INTO `t_sys_notic` VALUES ('swtzefa18a3bbb304b2a', 'wsdx', 'swtz', '0', '普通', '0', '', '关于“长波班”同学集体参加素质拓展培训的通知', '<p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">各位长波班的学员：</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">为加强“长波班”班级建设，增强团队凝聚力，学校决定开展“长波班”素质拓展培训，与学生处其他组织学生干部一同进行。现将有关事项通知如下：</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 31px\"><strong><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">一、培训时间：</span></strong><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">2014</span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">年10月26日全天</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 32px; TEXT-INDENT: 32px\"><strong><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black\">二、培训地点：</span></strong><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black\">巴南区樵坪基地</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 32px\"><strong><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">三、培训对象</span></strong> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">“长波班”全体同学（与心理互助中心、宿管会、学工助理团、“重邮e站”手机报、勤工助学中心 主要学生干部一同开展）</span></p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\"></span><strong><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">四、前期准备</span></strong> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">请各同学们提前仔细阅读素质拓展时间流程表及注意事项。</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><strong><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">五、要求</span></strong> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">请同学们按照要求准时到达集合地点坐车出发并返回，培训过程中遵守培训要求，避免出现意外事故。</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">&nbsp;</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">附件： 素质拓展培训时间流程表及注意事项。</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 宋体; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">&nbsp;</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">学生工作部（处）</span> </p><p style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 37px\"><span style=\"FONT-SIZE: 21px; FONT-FAMILY: 仿宋_GB2312; COLOR: black; LINE-HEIGHT: 150%\" courier=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nb