-- ----------------------------
-- Table structure for t_sys_notic
-- ----------------------------
DROP TABLE IF EXISTS `t_sys_notic`;
CREATE TABLE `t_sys_notic` (
  `nc_id` varchar(60) NOT NULL DEFAULT '' COMMENT ' 编号，无意义',
  `sys_id` varchar(10) NOT NULL DEFAULT '' COMMENT '系统编号',
  `nc_mode` varchar(10) NOT NULL DEFAULT '' COMMENT '模块名称(英文)',
  `nc_cat` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `nc_grade` varchar(10) NOT NULL DEFAULT '0' COMMENT '重要程度',
  `nc_scope` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `nc_user` varchar(3000) NOT NULL DEFAULT '',
  `nc_title` varchar(100) NOT NULL DEFAULT '',
  `nc_body` text NOT NULL,
  `nc_attfile` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `nc_click` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `nc_memo` varchar(50) NOT NULL DEFAULT '',
  `nc_status` varchar(10) NOT NULL DEFAULT '' COMMENT '审核状态',
  `nc_top` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `nc_color` varchar(50) NOT NULL DEFAULT '',
  `dept_id` varchar(10) NOT NULL DEFAULT '' COMMENT '发布部门编号',
  `dept_name` varchar(60) NOT NULL DEFAULT '' COMMENT '发布部门名称',
  `AUDIT_USER_ID` varchar(10) NOT NULL DEFAULT '' COMMENT '审核人编号',
  `AUDIT_USER_NAME` varchar(30) NOT NULL DEFAULT '' COMMENT '审核人姓名',
  `AUDIT_USER_TIME` varchar(20) NOT NULL DEFAULT '' COMMENT '审核时间',
  `puser_id` varchar(10) NOT NULL DEFAULT '',
  `puser_name` varchar(30) NOT NULL DEFAULT '',
  `puser_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`nc_id`),
  KEY `IX_T_NOTIC_nc_mode` (`nc_mode`),
  KEY `ix_t_notic_Puser_id` (`puser_id`),
  KEY `ix_t_notic_puser_time` (`nc_mode`,`puser_time`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of t_sys_notic
-- ----------------------------
DROP TRIGGER IF EXISTS `tiger_t_notic_name`;
DELIMITER ;;
CREATE TRIGGER `tiger_t_notic_name` BEFORE UPDATE ON `t_sys_notic` FOR EACH ROW SET new.PUSER_NAME = IFNULL((SELECT USER_NAME FROM T_SYS_USER WHERE USER_ID=new.puser_id),'')
,new.dept_id =IFNULL((select DEPT_ID from t_sys_user where user_id=new.puser_id),'')
,new.dept_name =IFNULL((select DEPT_NAME from t_sys_user where user_id=new.puser_id),'')
,new.AUDIT_USER_NAME=IFNULL((select DEPT_NAME from t_sys_user where user_id=new.AUDIT_USER_ID),'')
;;
DELIMITER ;
